import styled from 'styled-components';

export const Container = styled.div`
  width: 100%;
  height: 100vh;
  display: flex;
  flex-direction: column;
`

export const Content = styled.div`
  width: 100%;
  display: flex;
  flex-grow: 1;

  @media screen and (max-width: 1100px) {
    flex-direction: row;
  }
`
