import axios, { AxiosRequestConfig } from 'axios'
import { User } from '../models/user';
import { AddClickModel } from '../models/add-click';

export const config: AxiosRequestConfig = {
	//baseURL: 'http://18.231.116.149:443/api/',
	//baseURL: process.env.REACT_APP_API_URL,
  	baseURL: 'http://localhost:5050/api/',
	responseType: 'json',
	headers: {
		accept: 'application/json',
		'content-type': 'application/json',
	},
	timeout: 40000,
	withCredentials: false,
	maxRedirects: 5,
	validateStatus: (status: number) => status >= 200 && status < 300,
	transformResponse: (response) => {
		if (response !== null && response.error) {
			console.log(response.error);
		}
		const resp = JSON.parse(response)
		return resp;
	},
};

export const login = async (userInfo: any) => {
	config.url = 'login'
	config.method = 'post'
	config.data = userInfo
	return axios.request<any>(config);
};

export const loadUserData = async (accessToken: string) => {
	config.url = `loadUserData/${accessToken}`
	config.method = 'get'
	return axios.request<User>(config)
}

export const addClick = async (newClick: AddClickModel) => {
  config.url = 'add-click'
  config.method = 'post'
  config.data = newClick
  return axios.request<any>(config)
}

export const loadUserCampaings = async (idUser: number, accessToken: string) => {
	config.url = `load-campaings/${idUser}`
	config.method = 'get'
  if (config.headers) {
    Object.assign(config.headers, { 'x-access-token': accessToken })
  }
	return axios.request<any>(config)
}

export const loadCampaingSummary = async (idCampaing: number, accessToken: string) => {
	config.url = `load-campaing-summary/${idCampaing}`
	config.method = 'get'
  if (config.headers) {
    Object.assign(config.headers, { 'x-access-token': accessToken })
  }
	return axios.request<any>(config)
}

export const loadCampaing = async (idCampaing: number, accessToken: string) => {
	config.url = `load-campaing/${idCampaing}`
	config.method = 'get'
  if (config.headers) {
    Object.assign(config.headers, { 'x-access-token': accessToken })
  }
	return axios.request<any>(config)
}
