import { AddLinkButton, TabsContainer, HomePageContainer, Content } from './styles'
import $ from 'jquery'
import ModalNewLink from '../../components/Modals/NewLinkModal'
import Tab from './Tab'
import { useState } from 'react'
import CampaingsTab from './Campaing'

const HomePage = () => {
  const [tab1Selected, setTab1Selected] = useState<boolean>(true)
  const [tab2Selected, setTab2Selected] = useState<boolean>(false)

  const changeTabs = () => {
    console.log('hi')
    setTab1Selected(tab1Selected? false : true)
    setTab2Selected(tab2Selected? false : true)
  }
  return (
    <HomePageContainer>
      <TabsContainer>
        <Tab isSelected={tab1Selected} text='Campanhas' onClick={changeTabs}/>
        <Tab isSelected={tab2Selected} text='Meus Links' onClick={changeTabs}/>
      </TabsContainer>

      {tab2Selected && <AddLinkButton onClick={() => $('.AddLink').css('display', 'flex')}>+</AddLinkButton>}

      <Content>
        {tab1Selected? (
          <CampaingsTab campaings={[{id: 1, idUser: 1, name: 'Teste'}]}/>
        ) : (
          '2'
        )}
      </Content>

      <div className='modal AddLink'>
        <ModalNewLink />
      </div>
    </HomePageContainer>
  )
}

export default HomePage
