import styled from "styled-components";

export const HomePageContainer = styled.div`
  width: 100%;
`

export const TabsContainer = styled.div`      
  display: flex;
  justify-content: center;
  background-color: #222222;
`

export const AddLinkButton = styled.button`
  width: 60px;
  height: 60px;
  display: flex;
  border-radius: 30px;
  border: 4px solid #3f3d3d;
  font-size: 40px;
  line-height: 60px;
  background-color: white;
  color: #d47532; 
  justify-content: center;
  align-items: center;
  position: absolute;
  top: 60px;
  right: 20px;
  cursor: pointer;
  box-shadow: 0px 0px 10px 5px #3f3d3d;
  transition: border 0.3s, box-shadow 0.3s; color: 0.3s;

  &:hover {
    border: 4px solid #ec8f4d;
    box-shadow: 0px 0px 10px 5px #ec8f4d;
    color: #3f3d3d
  }
`

export const Content = styled.div`      
  width: 100%;
  height: 100%;
  justify-content: center;
`
