import styled from "styled-components";

export const CampaingPageContainer = styled.div`
  width: 100%;
  display: flex;
  flex-direction: column;
  transition: top;
  flex-grow: 1;
  overflow: auto;

  @media screen and (max-width: 750px) {
    height: 92vh;
  }
`

export const TabsContainer = styled.div`      
  display: flex;
  align-items: center;

  @media screen and (max-width: 750px) {
    margin: 10px 0px;
  }
`

export const ContentContainer = styled.div`
  padding-top: 10px;
  display: flex;
  flex-direction: column;
  height: 90vb;

  @media screen and (max-width: 750px) {
    padding-top: 0px;
  }


  ::-webkit-scrollbar {
    width: 10px;
  }
`
