import { ContainerTab, TabLink, Square, Circle } from './styles'
import $ from 'jquery'
import { useState } from 'react'

interface Props {
    text: string
    isSelected: boolean
    onClick: (tab: string) => void
}

const Tab = ({ text, isSelected, onClick }: Props) => {    
    const changeClass = () => {
        if (!isSelected) {
            const tabClass = $(`#${text}`).attr('class')?.split(' ')

            if (tabClass && tabClass[tabClass.length-1] === 'active') {
                $(`#${text}`).removeClass('active')
                $(`#squareBefore${text}`).removeClass('active')
                $(`#squareAfter${text}`).removeClass('active')
            } else {
                $(`#${text}`).addClass('active')
                $(`#squareBefore${text}`).addClass('active')
                $(`#squareAfter${text}`).addClass('active')
            }
            onClick(text)
        }
    }

    return (
        <ContainerTab onClick={changeClass}>
            <Circle className='before'></Circle>
            <Square className={isSelected? 'active' : ''} id={`squareBefore${text}`}></Square>
            <TabLink className={isSelected? 'active' : ''} id={text}>{text}</TabLink>
            <Square className={isSelected? 'active' : ''} id={`squareAfter${text}`}></Square>
            <Circle className='after'></Circle>
        </ContainerTab>
    )
}

export default Tab
