import { AddLinkButton, TabsContainer, HomePageContainer, Content } from './styles'
import $ from 'jquery'
import ModalNewLink from '../../components/Modals/NewLinkModal'
import { useEffect, useState } from 'react'
import CampaingsTab from './Campaing'
import CampaingModel from '../../models/campaing'
import { User } from '../../models/user'
import { loadUserCampaings } from '../../services'

interface Props {
  user?: User []
}

const HomePage = ({ user }: Props) => {
  const [tab1Selected, setTab1Selected] = useState<boolean>(true)
  const [tab2Selected, setTab2Selected] = useState<boolean>(false)
  const [campaings, setCampaings] = useState<CampaingModel[]>()

  const changeTabs = () => {
    setTab1Selected(tab1Selected? false : true)
    setTab2Selected(tab2Selected? false : true)
  }

  useEffect(() => {
    if(user) {
      loadUserCampaings(user[0].id, user[0].accessToken)
      .then(resp => {
        setCampaings(resp.data.campaings)
      })
      .catch(error => {
        console.log(error)
      })
    }
  }, [user])

  return (
    <HomePageContainer>
      <TabsContainer>

      </TabsContainer>

      {tab2Selected && <AddLinkButton onClick={() => $('.AddLink').css('display', 'flex')}>+</AddLinkButton>}

      <Content>
        {tab1Selected ? (
          <CampaingsTab campaings={campaings} user={user} />
        ) : (
          '2'
        )}
      </Content>

      <div className='modal AddLink'>
        <ModalNewLink />
      </div>
    </HomePageContainer>
  )
}

export default HomePage
