import { useEffect, useState } from 'react'
import FunilCards from '../../../components/CampaingElements/Funil/FunilCards'
import FunilLine from '../../../components/CampaingElements/Funil/FunilLine'
import ClickModel from '../../../models/click'
import { User } from '../../../models/user'
import { Container, TabHeader, FiltersContainer, SearchBar } from './styles'
import { CampaingSummaryModel } from '../../../models/campaing-summary'
import Loading from '../../../components/Loading'
import { CampaingDataModel } from '../../../models/campaing-data'

interface Props {
  user?: User []
  campaingData: CampaingDataModel
}

const ClicksTab = ({ user, campaingData }: Props) => {
  const [showClicks, setShowClicks] = useState<ClickModel[]>(campaingData.clicks)
  const [filteredSummary, setFilteredSummary] = useState<CampaingSummaryModel>()

  useEffect(() => {
    if (user) {
      let summary: CampaingSummaryModel = {
        id: campaingData.campaing.id,
        idUser: user[0].id,
        name: campaingData.campaing.name,
        income: 0,
        totalClicks: showClicks.length,
        totalProductPage: 0,
        totalCheckoutPage: 0, 
        totalPaidClicks: 0
      }

      showClicks.forEach(click => {
        summary.income += click.value
        summary.totalProductPage += click.viewedProductPage? 1 : 0
        summary.totalCheckoutPage += click.viewedCheckoutPage? 1 : 0 
        summary.totalPaidClicks += click.value > 0? 1 : 0
      })

      setFilteredSummary(summary)
    }

  }, [showClicks, campaingData, user])

  return (
    <Container>
      <TabHeader>
        <FiltersContainer>
          <SearchBar placeholder='Digite o termo que deseja buscar...'/>

        </FiltersContainer>

       {filteredSummary? (
        <>
          <FunilCards campaingSummary={filteredSummary} />
          <FunilLine campaingSummary={filteredSummary} />
        </>
       ) : (
        <Loading />
       )}
      </TabHeader>

    </Container>
  )
}

export default ClicksTab
