import { AddHabitButton, TabsContainer, HomePageContainer } from './styles'
import $ from 'jquery'
import ModalNewHabit from '../../components/Modals/NewHabitModal'
import Tab from './Tab'
import { useState } from 'react'

const HomePage = () => {
    const [tab1Selected, setTab1Selected] = useState<boolean>(true)
    const [tab2Selected, setTab2Selected] = useState<boolean>(false)

    const changeTabs = () => {
        setTab1Selected(tab1Selected? false : true)
        setTab2Selected(tab2Selected? false : true)

    }
    console.log(tab1Selected, tab2Selected)
    return (
        <HomePageContainer>
             <TabsContainer>
                <Tab isSelected={tab1Selected} text='Campanhas' onClick={changeTabs}/>
                <Tab isSelected={tab2Selected} text='Meus Links' onClick={changeTabs}/>
            </TabsContainer>

            {tab1Selected && <AddHabitButton onClick={() => $('.AddHabit').css('display', 'flex')}>+</AddHabitButton>}

            <div className='modal AddHabit'>
                <ModalNewHabit />
            </div>
        </HomePageContainer>
    )
}

export default HomePage
